import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import "./Artists.css"

export const ArtistList = ({searchTermState}) => {
    const [artists, setArtists] = useState([])
    const [countries, setCountries] = useState([])
    const [filteredArtists, setFiltered] = useState([])
    const [favorites, setFavorites] = useState({})


    const navigate = useNavigate()

    const localWavelandUser = localStorage.getItem("waveland_user")
    const wavelandUserObject = JSON.parse(localWavelandUser)

    useEffect(
        () => {
            fetch(`http://localhost:8088/artists?_expand=country`)
            .then(res => res.json())
            .then((artistArray) => {
                setArtists(artistArray)
                setFiltered(artistArray)
            })
        },
        []
        )
    useEffect(
        () => {
            fetch(`http://localhost:8088/countries`)
            .then(res => res.json())
            .then((countriesArray) => {
                setCountries(countriesArray)
            })
        },
        []
        )

    useEffect(

        () => {
            const searchedArtists = artists.filter(artist => {
                return artist?.country?.name.toLowerCase().startsWith(searchTermState.toLowerCase())
            })
            setFiltered(searchedArtists)
        },
        [ searchTermState ]
    )


    useEffect(
        () => {
            fetch(`http://localhost:8088/favorites?`)
            .then(res => res.json())
            .then((favoritesArray) => {
                // const singleFavoriteArtist = favoritesArray[0]
                const matchedFavorite = favotiif (artistObj !== favoritesArray.id)
                setFavorites(favoritesArray)
            })
        },
        []
        )



const handleFavoriteButton = (event, artistObj) => {
    event.preventDefault()
        favorites.map((favorite) => {
            if (artistObj.id === favorites.artistId) {
                window.alert("Artist Already in Favorite Listn")
                console.log("Artist Already in Favorite List")
            } else {
                window.alert("Artist Added to Favorites")
                console.log("Artist Added to Favorites")
                // TODO: Create the object to be saved to the API
                const newFavorite = {
                    userId: wavelandUserObject.id,
                    artistId: artistObj.id
                }
                fetch(`http://localhost:8088/favorites`, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify(newFavorite)
                })
                .then(res => res.json())
                .then(() => {
                    navigate("/favorites")
                }
                )
            }
    })
}


