                                    return <section className="artist" key={`Artist--${artist.id}`}>
                                        {/* <div>{artist.image}</div> */}
                                        <>
                                            <div className="image">                                   
                                                <img src={artist.image} alt="image" width={300} height={300}/>
                                            </div>
                                            <h2>{artist.name}</h2>
                                            <button className="favorites">Favorite</button>

                                            {/* <HeartSwitch
