import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import "./Tickets.css"

export const ArtistList = ({ searchTermState }) => {
    const [artists, setArtists] = useState([])
    const [filteredArtists, setFiltered] = useState([])
    const [emergency, setEmergency] = useState(false)
    const [openOnly, updateOpenOnly] = useState(false)
    const navigate = useNavigate()

    const localWavelandUser = localStorage.getItem("waveland_user")
    const wavelandUserObject = JSON.parse(localWavelandUser)

    useEffect(
        () => {
            const searchedArtists = artists.filter(artist => {
                return artist.description.toLowerCase().startsWith(searchTermState.toLowerCase())
            })
            setFiltered(searchedArtists)
        },
        [ searchTermState ]
    )

    useEffect(
        () => {
            if (emergency) {
                const emergencyArtists = artists.filter(artist => artist.emergency === true)
                setFiltered(emergencyArtists)
            }
            else {
                setFiltered(artists
                )
            }
        },
        [emergency]
    )

    useEffect(
        () => {
            fetch(`http://localhost:8088/artists`)
            .then(res => res.json())
            .then((artistArray) => {
                setArtists(artistArray)
            })
        },
        [] // When this array is empty, you are observing initial component state
    )

    useEffect(
        () => {
            if (wavelandUserObject.staff) {
                // For Employees
                setFiltered(artists)
            }
            else {
                // For Customers
                const myArtists = artists.filter(artist => artist.userId === wavelandUserObject.id)
                setFiltered(myArtists)
            }
        },
        [artists]
    )

    useEffect(
        () => {
            if (openOnly) {
            const openArtistArray = artists.filter(artist => {
                return artist.userId === wavelandUserObject.id && artist.dateCompleted === ""
            })
            setFiltered(openArtistArray)
        }
        else {
            const myArtists = artists.filter(Artist => Artist.userId === wavelandUserObject.id)
            setFiltered(myArtists)
        }
        },
        [ openOnly ]
    )


    return <>
    {
        wavelandUserObject.staff
        ? <>
            <button onClick={ () => { setEmergency(true) } }>Emergency Only</button>
            <button onClick={ () => { setEmergency(false) } }>Show All</button>
        </>
        : <>
            <button onClick={() => navigate("/Artist/create")}>Create Artist</button>
            <button onClick={() => updateOpenOnly(true)}>Open Artist</button>
            <button onClick={() => updateOpenOnly(false)}>All My Artists</button>
        </>
    }

        <h2>List of Artists</h2>

        <article className="Artists">
            {
                filteredArtists.map(
                    (Artist) => {
                        return <section className="Artist" key={`Artist--${Artist.id}`}>
                            <header>{Artist.description}</header>
                            <footer>Emergency: {Artist.emergency ? "ðŸ§¨" : "No"}</footer>
                        </section>
                    }
                )
            }
        </article>
    </>
}
