import { useEffect, useState } from "react"
import { CountrySelect } from "./CountrySelect"
import { GenreSelect } from "./GenreSelect"


export const NewArtistForm = () => {
    // TODO: Provide initial state for profile
    const [completeArtist, setCompleteArtist] = useState({})
    const [newArtist, updateNewArtist] = useState({})
    const [genres, setGenres] = useState({})
    const [countries, setCountries] = useState({})
    // const [artists, setArtists] = useState([])
    // const [filteredArtists, setFiltered] = useState([])
    // const [favorites, setFavorites] = useState({})
    // const [newFavorite, setNewFavorite] = useState(false)
    // const navigate = useNavigate()

    const localWavelandUser = localStorage.getItem("waveland_user")
    const wavelandUserObject = JSON.parse(localWavelandUser)

    console.log(countries)



    useEffect(() => {
        fetch(`http://localhost:8088/users?id=${wavelandUserObject.id}`)
        .then(res => res.json())
        .then((data) => {
            const userObjects = data[0]
                updateNewArtist(userObjects)
            }  
        )
        }, [])

    useEffect(() => {
        fetch(`http://localhost:8088/countries`)
        .then(res => res.json())
        .then((data) => {
            const countryObjects = data
                setCountries(countryObjects)
            }  
        )
        }, [])

    useEffect(() => {
        fetch(`http://localhost:8088/genre`)
        .then(res => res.json())
        .then((data) => {
            const genreObjects = data
                setGenres(genreObjects)
            }  
        )
        }, [])



    const [feedback, setFeedback] = useState("")
    useEffect(() => {
        if (feedback !== "") {
            // Clear feedback to make entire element disappear after 3 seconds
            setTimeout(() => setFeedback(""), 3000);
        }
    }, [feedback])


    const handleSaveButtonClick = (event) => {
        event.preventDefault()

        /*
            TODO: Perform the PUT fetch() call here to update the profile.
            Navigate user to home page when done.
        */
        return fetch(`http://localhost:8088/artists/${completeArtist.id}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(completeArtist)
        })
            .then(res => res.json())
            .then(() => {
                setFeedback("New Artist Successfully Saved")
            })
    }



    // console.log(newArtist.name)
    return (
        <>
        <div className={`${feedback.includes("Error") ? "error" : "feedback"} ${feedback === "" ? "invisible" : "visible"}`}>
            {feedback}
        </div>
        <form className="newArtist">
            <h2 className="newArtist__title">New Artist Information</h2>
            <fieldset>
                <div className="form-group">
                    <label htmlFor="newArtistName">Add Artist Name:</label>
                    <input
                        required autoFocus
                        type="text"
                        className="form-control"
                        value={newArtist.name}
                        onChange={
                            (evt) => {
                                // TODO: Update username property
                                    const copy = {...newArtist}
                                    copy.userName = evt.target.value
                                    updateNewArtist(copy)
                            }
                        } />
                </div>
            </fieldset>
            <fieldset>
                <div className="form-group">
                    <label htmlFor="newArtistImage">Add Image URL:</label>
                    <input type="text"
                        className="form-control"
                        value={newArtist.image}
                        onChange={
                            (evt) => {
                                // TODO: Update email property
                                const copy = {...newArtist}
                                    copy.email = evt.target.value
                                    updateNewArtist(copy)
                            }
                        } />
                </div>
            </fieldset>
            <select onChange={
                (event) => {
                    const copy = { ...countries }
                    copy.name = event.target.value
                    setCountries(copy)
                }
            } className="country_select">
                <option value={0}>Select Artist Country</option>
                {countries.map((country) => <CountrySelect key={`country--${country?.id}`} country={country} />)}
            </select>
            <select onChange={
                (event) => {
                    const copy = { ...genres }
                    copy.name = event.target.value
                    setGenres(copy)
                }
            } className="genre_select">
                <option value={0}>Select Artist Genre</option>
                {genres.map((genre) => <GenreSelect key={`genre--${genre?.id}`} genre={genre} />)}
            </select>
            <button
                onClick={(clickEvent) => handleSaveButtonClick(clickEvent)}
                className="btn btn-primary">
                Save New Artist
            </button>

        </form>
        </>
    )
}
